"use client"
import * as React from "react"
import * as AccordionPrimitive from "@radix-ui/react-accordion"
import { cva, type VariantProps } from "class-variance-authority"
import { ChevronDown } from "lucide-react"

// registryDependency
import { cn } from "@/lib/utils"
import { useFyskConfig } from "@/components/fysk-provider"

const accordionVariants = cva("w-full transition-all duration-500", {
    variants: {
        variant: {
            default: "",
            bordered: "border border-border rounded-xl overflow-hidden shadow-sm bg-card",
        },
    },
    defaultVariants: {
        variant: "default",
    },
})

const AccordionContext = React.createContext<{ variant: "default" | "bordered" }>({
    variant: "default",
})

const Accordion = React.forwardRef<
    React.ComponentRef<typeof AccordionPrimitive.Root>,
    React.ComponentPropsWithoutRef<typeof AccordionPrimitive.Root> & VariantProps<typeof accordionVariants>
>(({ className, variant = "default", ...props }, ref) => (
    <AccordionContext.Provider value={{ variant: variant || "default" }}>
        <AccordionPrimitive.Root
            ref={ref}
            className={cn(accordionVariants({ variant, className }))}
            {...props}
        />
    </AccordionContext.Provider>
))
Accordion.displayName = "Accordion"

const AccordionItem = React.forwardRef<
    React.ComponentRef<typeof AccordionPrimitive.Item>,
    React.ComponentPropsWithoutRef<typeof AccordionPrimitive.Item>
>(({ className, ...props }, ref) => {
    const { variant } = React.useContext(AccordionContext)
    return (
        <AccordionPrimitive.Item
            ref={ref}
            className={cn(
                "border-b border-border last:border-0",
                variant === "bordered" && "bg-card",
                className
            )}
            {...props}
        />
    )
})
AccordionItem.displayName = "AccordionItem"

const AccordionTrigger = React.forwardRef<
    React.ComponentRef<typeof AccordionPrimitive.Trigger>,
    React.ComponentPropsWithoutRef<typeof AccordionPrimitive.Trigger> & {
        TriggerIcon?: React.ReactNode
    }
>(({ className, children, TriggerIcon, ...props }, ref) => {
    const { variant } = React.useContext(AccordionContext)
    const config = useFyskConfig();

    const finalTriggerIcon = TriggerIcon || config.icons?.chevronDown || <ChevronDown />;

    return (
        <AccordionPrimitive.Header className="flex">
            <AccordionPrimitive.Trigger
                ref={ref}
                className={cn(
                    "group flex flex-1 items-center justify-between py-4 font-medium text-sm transition-all bg-secondary text-secondary-foreground hover:bg-secondary/50 active:scale-[0.99] focus-visible:ring-2 focus-visible:ring-ring",
                    variant === "bordered" && "px-4",
                    variant === "default" && "px-1",
                    className
                )}
                {...props}
            >
                {children}
                {finalTriggerIcon && <span className="[&_svg]:group-data-[state=closed]:rotate-0 [&_svg]:group-data-[state=open]:rotate-180 [&_svg]:h-4 [&_svg]:w-4 [&_svg]:shrink-0 [&_svg]:text-muted-foreground [&_svg]:transition-transform [&_svg]:duration-300 [&_svg]:ease-in-out">{finalTriggerIcon}</span>}
            </AccordionPrimitive.Trigger>
        </AccordionPrimitive.Header>
    )
})
AccordionTrigger.displayName = AccordionPrimitive.Trigger.displayName

const AccordionContent = React.forwardRef<
    React.ComponentRef<typeof AccordionPrimitive.Content>,
    React.ComponentPropsWithoutRef<typeof AccordionPrimitive.Content>
>(({ className, children, ...props }, ref) => {
    const { variant } = React.useContext(AccordionContext)
    return (
        <AccordionPrimitive.Content
            ref={ref}
            className="overflow-hidden text-sm data-[state=closed]:animate-accordion-up data-[state=open]:animate-accordion-down"
            {...props}
        >
            <div className={cn(
                "py-4 text-foreground bg-background/50",
                variant === "bordered" && "px-4",
                variant === "default" && "px-1",
                className
            )}>
                {children}
            </div>
        </AccordionPrimitive.Content>
    )
})
AccordionContent.displayName = AccordionPrimitive.Content.displayName

export { Accordion, AccordionItem, AccordionTrigger, AccordionContent }
