"use client"

import * as React from "react"
import * as AspectRatioPrimitive from "@radix-ui/react-aspect-ratio"
import { cn } from "@/lib/utils"

interface AspectRatioProps extends Omit<React.ComponentPropsWithoutRef<typeof AspectRatioPrimitive.Root>, "ratio"> {
    ratio?: number | "16/9" | "4/3" | "1/1" | "21/9"
}

const AspectRatio = React.forwardRef<
    React.ComponentRef<typeof AspectRatioPrimitive.Root>,
    AspectRatioProps
>(({ ratio = "16/9", className, ...props }, ref) => {
    let numericRatio = 16 / 9
    if (typeof ratio === "number") {
        numericRatio = ratio
    } else {
        switch (ratio) {
            case "1/1": numericRatio = 1; break;
            case "4/3": numericRatio = 4 / 3; break;
            case "16/9": numericRatio = 16 / 9; break;
            case "21/9": numericRatio = 21 / 9; break;
        }
    }

    return (
        <AspectRatioPrimitive.Root
            ref={ref}
            ratio={numericRatio}
            className={cn("overflow-hidden rounded-md", className)}
            {...props}
        />
    )
})
AspectRatio.displayName = "AspectRatio"

export { AspectRatio }
