"use client"

import { format } from "date-fns"
import { cn } from "@/lib/utils"
import { Button } from "@/components/fysk/button"
import { Calendar } from "@/components/fysk/calendar"
import {
    Popover,
    PopoverContent,
    PopoverTrigger,
} from "@/components/fysk/popover"

export interface DatePickerProps {
    date?: Date
    setDate?: (date: Date | undefined) => void
    placeholder?: string
    className?: string
    Icon?: React.ReactNode
}

const DefaultCalendarIcon = ({ className }: { className?: string }) => <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M8 2v4" /><path d="M16 2v4" /><rect width="18" height="18" x="3" y="4" rx="2" /><path d="M3 10h18" /></svg>

export function DatePicker({ date, setDate, placeholder = "Pick a date", className, Icon }: DatePickerProps) {
    return (
        <Popover>
            <PopoverTrigger asChild>
                <Button
                    variant={"outline"}
                    className={cn(
                        "w-[280px] justify-start text-left font-normal",
                        !date && "text-muted-foreground",
                        className
                    )}
                >
                    {
                        Icon || <DefaultCalendarIcon className="mr-2 h-4 w-4" />
                    }
                    {date ? format(date, "PPP") : <span>{placeholder}</span>}
                </Button>
            </PopoverTrigger>
            <PopoverContent className="w-auto p-0">
                <Calendar
                    mode="single"
                    selected={date}
                    onSelect={setDate}
                />
            </PopoverContent>
        </Popover>
    )
}
