"use client"

import * as React from "react"
import { cn } from "@/lib/utils"

const InputGroup = React.forwardRef<
    HTMLDivElement,
    React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
    <div
        ref={ref}
        className={cn(
            "relative flex w-full items-center rounded-md border border-input bg-background overflow-hidden transition-all focus-within:ring-2 focus-within:ring-ring focus-within:border-primary",
            className
        )}
        {...props}
    />
))
InputGroup.displayName = "InputGroup"

const InputGroupText = React.forwardRef<
    HTMLSpanElement,
    React.HTMLAttributes<HTMLSpanElement>
>(({ className, ...props }, ref) => (
    <span
        ref={ref}
        className={cn(
            "flex items-center px-3 text-sm text-muted-foreground bg-muted border-r border-input h-10",
            className
        )}
        {...props}
    />
))
InputGroupText.displayName = "InputGroupText"

const InputGroupInput = React.forwardRef<
    HTMLInputElement,
    React.InputHTMLAttributes<HTMLInputElement>
>(({ className, ...props }, ref) => (
    <input
        ref={ref}
        className={cn(
            "flex h-10 w-full bg-transparent px-3 py-2 text-sm outline-none placeholder:text-muted-foreground disabled:cursor-not-allowed disabled:opacity-50",
            className
        )}
        {...props}
    />
))
InputGroupInput.displayName = "InputGroupInput"

export { InputGroup, InputGroupText, InputGroupInput }
