"use client"

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"
import { cn } from "@/lib/utils"

const ItemVariants = cva(
    "group relative flex items-center gap-3 rounded-lg px-3 py-2 text-sm transition-all duration-150 cursor-pointer",
    {
        variants: {
            variant: {
                default: "hover:bg-accent hover:text-accent-foreground",
                active: "bg-primary text-primary-foreground font-medium",
                ghost: "hover:bg-muted/50 text-muted-foreground hover:text-foreground",
                outline: "border border-border hover:border-primary/50 hover:bg-primary/5",
            },
            size: {
                sm: "px-2 py-1 text-xs",
                md: "px-3 py-2",
                lg: "px-4 py-3 text-base",
            }
        },
        defaultVariants: {
            variant: "default",
            size: "md",
        }
    }
)

export interface ItemProps
    extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof ItemVariants> {
    icon?: React.ReactNode
    active?: boolean
    state?: "idle" | "loading" | "success" | "error"
}

const Item = React.forwardRef<HTMLDivElement, ItemProps>(
    ({ className, variant, size, icon, active, state = "idle", children, ...props }, ref) => {
        return (
            <div
                ref={ref}
                className={cn(
                    ItemVariants({ variant: active ? "active" : variant, size, className }),
                    state === "loading" && "opacity-70 pointer-events-none",
                    state === "error" && "border-destructive text-destructive"
                )}
                data-state={state}
                {...props}
            >
                {icon && <span className="shrink-0 size-4">{icon}</span>}
                <div className="flex-1 truncate">{children}</div>
                {state === "loading" && (
                    <div className="absolute right-3 animate-spin rounded-full h-3 w-3 border-2 border-primary border-t-transparent"></div>
                )}
            </div>
        )
    }
)
Item.displayName = "Item"

export { Item, ItemVariants }
